Date now = new Date();
int day = now.getDate();
String dayStr = day.toString();
return dayStr;


Date now = new Date();
int month = now.getMonth() + 1;
String monthStr = month.toString();
return monthStr;


Date now = new Date();
int year = now.getYear() + 1900;
String yearStr = year.toString();
return yearStr;
